$(document).ready(function(){
	var numeValid = 0;
	var prenumeValid = 0;
	var parolaValid = 0;
	var telValid = 0;
	var mailValid = 0;
	var dataValid = 0;
	
	$("#nume").keyup(function(){
		var re = /^[a-z0-9]+$/;
		if (re.test($(this).val())){
			$("#1").css({"background-color": "#00ff00"});
			numeValid = 1;
		} else{
			$("#1").css({"background-color": "#ff0000"});
			numeValid = 0;
		}
	});
	
	$("#prenume").keyup(function(){
		var re = /^[a-z0-9]+$/;
		if (re.test($(this).val())){
			$("#2").css({"background-color": "#00ff00"});
			prenumeValid = 1;
		} else{
			$("#2").css({"background-color": "#ff0000"});
			prenumeValid = 0;
		}
	});
	
	$("#parola").keyup(function(){
		var re = /^(?=.*[0-9])(?=.*[a-z])(?=.*[A-Z])(?=.*[!])/;
		if (re.test($(this).val())){
			$("#3").css({"background-color": "#00ff00"});
			parolaValid = 1;
		} else{
			$("#3").css({"background-color": "#ff0000"});
			parolaValid = 0;
		}
	});
	
	$("#telefon").keyup(function(){
		var re = /^[\(]\+[0-9][0-9][\)]+[0-9][0-9][0-9]\s[0-9][0-9][0-9]\s[0-9][0-9][0-9]$/;
		if (re.test($(this).val())){
			$("#4").css({"background-color": "#00ff00"});
			telValid = 1;
		} else{
			$("#4").css({"background-color": "#ff0000"});
			telValid = 0;
		}
	});
	
	$("#mail").keyup(function(){
		var re = /^[a-z0-9]+@[a-z0-9.]+\.[a-z]{2,4}$/;
		if (re.test($(this).val())){
			$("#5").css({"background-color": "#00ff00"});
			mailValid = 1;
		} else{
			$("#5").css({"background-color": "#ff0000"});
			mailValid = 0;
		}
	});
	
	$("#data").keyup(function(){
		var re;
		if ($("#format").val() == "zz/ll/aaaa"){
			re = /^(0?[1-9]|[12][0-9]|3[01])[\/](0?[1-9]|1[012])[\/]\d{4}$/;
		}
		else if ($("#format").val() == "ll/zz/aaaa"){
			re = /^(0?[1-9]|1[012])[\/](0?[1-9]|[12][0-9]|3[01])[\/]\d{4}$/;
		}
		else{
			re = /^(0?[1-9]|[12][0-9]|3[01])[\/](0?[1-9]|1[012])[\/]\d{2}$/;
		}
		if (re.test($(this).val())){
			$("#6").css({"background-color": "#00ff00"});
			dataValid = 1;
		} else{
			$("#6").css({"background-color": "#ff0000"});
			dataValid = 0;
		}
	});
	
	$("#submit").click(function(){
		if (numeValid == 1 && prenumeValid == 1 && parolaValid == 1 && mailValid == 1 && telValid == 1 && dataValid == 1){
			alert("Totul e bine");
		}
		else{
			alert("Fiecare bulina trebuie sa fie verde");
		}
	});
});
